<script setup lang="ts">
import { TComplexNew, TUniqueOffer } from "@/app/types/complex";
import MyCheckbox from "@/shared/UI/MyCheckbox.vue";
import MyInput from "@/shared/UI/MyInput.vue";
import MySelect from "@/shared/UI/MySelect.vue";
import MyTextarea from "@/shared/UI/MyTextarea.vue";
import FormItemLayout from "@/shared/UI/formItemLayout.vue";
import FormLayout from "@/shared/UI/modalLayout.vue";
import MyButton from "@/shared/UI/MyButton.vue";
import { reactive, ref } from "vue";

const emits = defineEmits<{
  (e: "closeModel"): void;
}>();
defineProps<{
  isOpen: boolean;
}>();

const titleForm = ref<string>("Добавить комплекс");
const titleFromNewBuild = ref<boolean>(false);
const newComplex = reactive<TComplexNew>({
  type: "",
  idDomClick: "",
  country: "",
  region: "",
  address: "",
  metro: "",
  title: "",
  uniqueOffer: [],
});

let uniqueOffer = reactive<(TUniqueOffer & { id: number })[]>([
  {
    id: 0,
    title: "",
    description: "",
  },
]);

const addNewUTP = () => {
  uniqueOffer.push({
    id: newComplex.uniqueOffer.length,
    title: "",
    description: "",
  });
};

const saveUtp = () => {
  console.log(
    uniqueOffer[uniqueOffer.length - 1].title !== "" &&
      uniqueOffer[uniqueOffer.length - 1].description !== ""
  );
  if (
    uniqueOffer[uniqueOffer.length - 1].title !== "" &&
    uniqueOffer[uniqueOffer.length - 1].description !== ""
