    uniqueOffer[uniqueOffer.length - 1].title !== "" &&
    uniqueOffer[uniqueOffer.length - 1].description
  ) {
    newComplex.uniqueOffer.push(uniqueOffer[uniqueOffer.length - 1]);
  }
};

const editUtp = (index: number) => {
  uniqueOffer = [];
  uniqueOffer.unshift({ id: index, ...newComplex.uniqueOffer[index] });
  newComplex.uniqueOffer.splice(index, 1);
};

const deleteUtp = (index: number) => {
  newComplex.uniqueOffer.splice(index, 1);
  uniqueOffer.push({
    id: newComplex.uniqueOffer.length,
    title: "",
    description: "",
  });
};

